def age_groups(val):
    """
    Description: This function is created for bucketing the age into various groups.

    Input: It accepts only one parameter:
        1. val : Age value

    Return: Age group
    """
    if val >= 30 and val <=40:
        return 'Group1'
    if val >= 41 and val <=50:
        return 'Group2'
    if val >= 51 and val <=60:
        return 'Group3'
    if val >= 61 and val <=70:
        return 'Group4'
    if val >= 71:
        return 'Group5'
