from datetime import date

def checkSpeed(speed):
    if speed <=60: print("Ticket No")
    elif speed >60 and speed <=80: print("Small Ticket")
    elif speed >80: print("Big Ticket")

def check(speed, birthday):
   today= date.today().strftime("%Y-%m-%d")
   if today == birthday:
     checkSpeed(speed-5)
   else:
     checkSpeed(speed)

