def create_decades(df):

  y=[]
  value=0
  x=1111
  years = []
  decades = []
  df['date'].fillna(0)
  df['date'] = df['date'].astype("str")
  for i in df.index:
    years.append(df['date'].str.split("-")[i][0])

  for year in df['year']:
    if year == 'None':
      y.append(x)
    elif year == 'nan':
      y.append(x)  
    else:
      value=int(year)
      y.append(value)


  for x in y:
    if x == 1111:
        decades.append("None")
    elif 1970 <= x < 1980:
        decades.append("70s")
    elif 1980 <= x < 1990:
        decades.append("80s")
    elif 1990 <= x < 2000:
        decades.append("90s")
    elif 2000 <= x < 2010:
        decades.append("00s")
    elif 2010 <= x < 2020:
        decades.append("10s")
    elif 2020 <= x < 2030:
        decades.append("20s")


  df['decade'] = decades
  df = df[['artist','title','album','decade','year','date','lyric']]
  return df

