def vertlabel(plot):
    for p in plot.patches[0:]:
        h = p.get_height()
        x = p.get_x()+p.get_width()/2.
        if h != 0:
            plot.annotate("%g" % p.get_height(), xy=(x,h), xytext=(0,4), rotation=90, 
                           textcoords="offset points", ha="center", va="bottom")
