# Remove URLs

def remove_urls(text):
    return re.sub(r'\w+:\/{2}[\d\w-]+(\.[\d\w-]+)*(?:(?:\/[^\s/]*))*', '', text)
train['Content']=train['OriginalTweet'].apply(lambda x:remove_urls(x))

