from sklearn.base import BaseEstimator, TransformerMixin
class DataFrameSelection(BaseEstimator, TransformerMixin):
    def __init__(self, attrs):
        self.attrs = attrs
    def fit(self, X, y=None):
        return self
    def transform(self, X, y=None):
        return X[attrs]

