def convert_result(row):
    if row.result == True:
        row.result = 1
    else:
        row.result = 0
    return row

def convert_att(row):
    if row.attempted == True:
        row.attempted = 1
    else:
        row.attempted = 0
    return row

non_duplicated = non_duplicated.apply(convert_result, axis = 1)
non_duplicated = non_duplicated.apply(convert_att, axis = 1)
