# function that returns a classification for a star

star_types = ['O','B','A','F','G','K','M']

def spectral_classify(temp):
    if temp > 33000:
        return star_types[0]
    elif temp > 10000:
        return star_types[1]
    elif temp > 7500:
        return star_types[2]
    elif temp > 6000:
        return star_types[3]
    elif temp > 5200:
        return star_types[4]
    elif temp > 3700:
        return star_types[5]
    else:
        return star_types[6]
