source.b5209013:
%%plantuml

@startuml

frame "training phase" {

  folder "preprocessing" as TRPPP {
    card labels as L
    file "training data" as TRD
    rectangle "feature extraction" as TRFE
  }

  folder learning {
    component "machine learning algorithm" as MLA
  }

  L --> MLA
  TRD --> TRFE
  TRFE --> MLA
}

frame "test phase" {

  folder "preprocessing" as TSPPP {
    file "test data" as TSD
    rectangle "feature extraction" as TSFE
  }

  folder evaluating {
    component "model" as M
  }

  TSD --> TSFE
  TSFE --> M
}

MLA -right-> M

@enduml
