def clean_list(cuisines):
    clean= []
    for value in cuisines:
        if value[0] == ' ':
            value = str(value[1:])
        clean.append(value)
    return clean

def count_dic(cuisines):
    clean = clean_list(cuisines)
    dic = {}
    for key in clean:
        if key in dic:
            dic[key] += 1
        else:
            dic[key] = 1
    return dic


