cards = {1: 4, 2: 4, 3: 4, 4: 4, 5: 4, 6: 4, 7: 4, 8: 4, 9: 4, 10: 16}
total_cards_count = 52
border = 21
d_border = 17
max_dealer_hit_count = 6


def should_hit(player_total, dealer_card_val, player_aces):
    """Return True if the player should hit (request another card) given the current game
    state, or False if the player should stay. player_aces is the number of aces the player has.
    """
    p_overdraft_chance = get_overdraft_chance(player_total, player_aces)
    d_win_chance = get_dealer_win_chance_aces(player_total, dealer_card_val, player_aces)
    return p_overdraft_chance < 0.5 < d_win_chance


def get_overdraft_chance(player_total, player_aces):
    diff = 21 - player_total - player_aces
    bad_count = 0
    for key, val in cards.items():
        if key > diff:
            bad_count += val
    return bad_count / total_cards_count


