def get_dealer_win_chance_aces(player_total, dealer_card_val, player_aces):
    return max(map(lambda x: get_dealer_win_chance_no_aces(x, dealer_card_val),
                   get_possible_player_values(player_total, player_aces)))


def get_dealer_win_chance_no_aces(player_total, dealer_card_val):
    bottom_border = max(d_border, player_total) - dealer_card_val
    top_border = border - dealer_card_val
    return get_dealer_win_chance(max_dealer_hit_count, bottom_border, top_border)


def get_possible_player_values(player_total, player_aces):
    values = [player_total + player_aces * 1,
              player_total + player_aces * 11,
              player_total + max(0, (player_aces - 1) * 11 + 1),
              player_total + max(0, (player_aces - 2) * 11 + 2),
              player_total + max(0, (player_aces - 3) * 11 + 3),
              ]
    return list(dict.fromkeys(values))


def get_dealer_win_chance(card_count, bottom_border, top_border):
    if bottom_border <= 0 <= top_border and card_count >= 0:
        return 1
    elif (bottom_border > 0 and card_count == 0) or top_border < 0:
        return 0
    else:
        micro_total = 0
        for key in cards.keys():
            micro_total += cards.get(key) * \
                           get_dealer_win_chance(card_count - 1, bottom_border - key,
                                                 top_border - key) / total_cards_count
        return micro_total
