def get_full_answer(gg):

    full_answer = {}

    for i in range(len(gg)):
        for j in range(len(gg)):
            if i < j:
                set_i = set(gg[str(i)])
                set_j = set(gg[str(j)])
                full_answer[str(i)+'_'+str(j)] = str(len(set_i))+'_'+\
                                                 str(len(set_j))+'_' + \
                                                 str(len(set_i.intersection(set_j)))

    return full_answer

