def number_of_colors(row):
    if row['Color1'] == 0:
        return 0
    elif (row['Color2'] != 0 and row['Color3'] == 0):
        return 2
    elif (row['Color2'] != 0 and row['Color3'] != 0):
        return 3
    else:
        return 1

train['number_of_colors'] = train.apply(number_of_colors, axis=1)
test['number_of_colors'] = test.apply(number_of_colors, axis=1)

