# 删除常量，如果最多数占总量的90%，则认为它是常量，进行删除
del_var = []
for var in cat_features:
    if train_data[var].value_counts()[0] / train_data.shape[0] > 0.9:
        del_var.append(var)
cat_features = [var for var in cat_features if var not in del_var]

