#We formatted the 'time' column to two columns i.e., 'month' and 'day'.
def month(x):
   m = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']
   return m[int(x.split('-')[1])-1] 
def day(x):
     return int(x.split('-')[2]) 
engagement['month'] = engagement['time'].apply(month)
engagement['day'] = engagement['time'].apply(day)
