def police_fine(speed,birthday,currentdate):
    if birthday == currentdate:
        speed = speed - 5

    if speed<60:
        print('No Ticket')
    elif speed>60 & speed<80:
        print('Small Ticket')
    else:
        print('Big Ticket')

police_fine(50,520,520)
