import datetime

## is valid for discount
def is_valid(birthdate):
    day, month, year = map(int, birthdate.split('/'))
    current_date = datetime.datetime.now()
    if current_date.day == day and current_date.month == month:
        return True    
    return False

def get_ticket(speed, birthdate):
    ## if today is his/her birhday he/she got discount
    if is_valid(birthdate):
        speed = speed - 5

    if speed < 60:
        status = 'no ticket'
    elif 60 <= speed < 80:
        status = 'small ticket'
    else:
        status = 'big ticket'
    return status

print(get_ticket(84, '24/8/2021'))
print(get_ticket(84, '24/7/2021'))
