def weight_variable(dim):
    w_init = tf.truncated_normal(dim,stddev=0.1)
    return tf.Variable(w_init)

def bias_variable(dim):
    b_init = tf.constant(0.2, shape = dim,)

    return tf.Variable(b_init)

