class users():
  def __init__(self, name):
    self.name = name

  def greetings(self):
    print("Hello ", self.name.title())

class admin(users):
  def __init__(self, name):
    super().__init__(name)

  def describe_admin(self):
    print(self.name.title() + " is an admin.")

admin1 = admin('James')
admin1.greetings()
admin1.describe_admin()

# output
# Hello, James
# James is admin.

user1 = users('Jessica')
user1.greetings()

# output
# Hello, Jessica
