def evaluate_simulation(sim_df, all_data):
    Fit = 0.0
    for region in sim_df.columns:
        Fit += (sim_df[region].astype('float').corr(all_data[region].astype('float')))**2
    Fit = np.sqrt(Fit/6.0)
    return 1 - Fit

