pie_list = list()
bar_list = list()
line_list = list()

for i in cat_var:
    if len(df[i].unique())<=5:
        pie_list.append(i)
    elif len(df[i].unique())<=12:
        bar_list.append(i)
    else:
        line_list.append(i)

print('Features with 5 levels   \n',pie_list,'\n\n',
      'Features with 5-10 levels\n',bar_list,'\n\n',
      'Features with >10 levels \n',line_list)

