def find_rating_score(movie_1, movie_2):
    rating_1 = movie_1['rating']
    rating_2 = movie_2['rating']
    if rating_1 == 'U':
        recom = 1.0

    elif rating_1 == 'Y':
        if rating_2 == 'R':
            recom = 0.0
        elif rating_2 == 'PG':
            recom = 0.25
        elif rating_2 == 'U':
            recom = 1.0
        else:
            recom = 0.0

    elif rating_1 == 'PG':
        if rating_2 == 'R':
            recom = 0.0
        elif rating_2 == 'Y':
            recom = 0.75
        elif rating_2 == 'U':
            recom = 1.0
        else:
            recom = 0.0

    elif rating_1 == 'R':
        if rating_2 == 'Y':
            recom = 0.25
        elif rating_2 == 'PG':
            recom = 0.5
        elif rating_2 == 'U':
            recom = 1.0
        else:
            recom = 0.0

    else:
        recom = 0.0

    return recom 

