def impute(pclass, elem, pclass_dict): 
    """欠損値の補完"""
    if pd.isnull(elem):
        return pclass_dict[pclass]
    else:
        return elem

def impute_age(cols):
    """Age の欠損値を補完"""
    pclass=cols[1]
    age=cols[0]
    pclass_age = {1: 39, 2: 30, 3: 25}

    return impute(pclass, age, pclass_age)

