def readImg(lis):
    ret = []
    for i in lis:
        img = cv2.resize(cv2.cvtColor(cv2.imread('../input/train_images/'+i+'.png'), cv2.COLOR_BGR2RGB), (300,300))
        ret.append(img)
    return ret

