def clean_loc(x):
    if x == 'None':
        return 'None'
    elif x == 'Corona' or x =='Corona Virus' or x == 'Covid-19':
        return 'Covid-19'
    elif 'Virus' in x or 'Viral' in x:
        return 'Virus'    
    elif 'Viruses' in x:
        return 'Viruses'
    elif 'Virology' in x:
        return 'Virology'
    elif 'Vaccine' in x and 'Vaccines' in x and 'Vaccinantion' in x:
        return 'Vaccine'
    elif x in top_loc:
        return x
    else: return 'Others'

meta_dt['title_clean'] = meta_dt['title'].apply(lambda x: clean_loc(str(x)))
