    """
    Computes overlapping distance between two lists of integer
    between 1 and 7. 8 is wildcard
    """
    p = list(p)
    q = list(q)

    if p==q:
        return 0

    # Nope
    if 8 in q and 8 in p:
        # Dist between 1238567 and 1278543 or reverse
        return 7

    if 8 in q:
        min_dist = 8
        for i in range(1,8):
            q2 = list(q)
            q2[q2.index(8)] = i
            for j in range(1,8):
                if p[j:]==q2[:-j]:
                    if min_dist > j:
                        min_dist = j
                        break
        return min_dist

    if 8 in p:
        min_dist = 8
        for i in range(1,8):
