                        perm = tuple(list(appends[index][i][0])+list(perm3)+list(appends[index][i][1]))
                        print("Wrote to tour:",perm)
                        break
            else:
                for k in range(1,8):
                    perm2 = list(perm)
                    perm2[perm2.index(8)] = k
                    if tuple(perm2) in perms:
                        perm = tuple(perm2)
                        break
        else:
