def add_value(value,name):
    img = cv2.imread("../input/images/peppers.png", cv2.IMREAD_COLOR)
    height,width = len(img),len(img[0])
    for i in range(0,height):
        for j in range(0,width):
            old = img[i][j][0]
            new = old + value
            if new>255:
                img[i][j]=[255,255,255]
            elif new<0:
                img[i][j]=[0,0,0]
            else:
                img[i][j]=[new,new,new]
    cv2.imwrite(f"{name}.png", img)

