def measure_income_compare(data):
    class_list = [0, 0, 0, 0, 0]
    for i in data['median_income']:
        if i >= 0 and i < 1.5:
            class_list[0] +=1
        elif i >= 1.5 and i < 3:
            class_list[1] +=1
        elif i >= 3 and i < 4.5:
            class_list[2] +=1
        elif i >= 4.5 and i < 6:
            class_list[3] +=1
        else: class_list[4] +=1
    return class_list

