from IPython.display import display, HTML
def highlight_selected_text(text,body_text,sentiment):
    body_text = body_text.strip()
    color = {
        "positive": "green",
        "negative": "red",
        "neutral": "blue",
    }[sentiment]

    highlighted = f'<span style="color: {color}; font-weight: bold">{text}</span>'
    return body_text.replace(text, highlighted)
def  show_highlight(text,body_text,sentiment):
    text =  highlight_selected_text(text,body_text,sentiment)
    x = pd.DataFrame([text],columns=['.'])
    d =transpose(x).style.set_table_styles([
    {'selector': 'th.col_heading', 'props': 'font-size: 0em;'},
    {'selector': 'th.col_heading.level0', 'props': 'font-size: 0em;'},
    {'selector': 'td', 'props': 'text-align: left; font-size: 1.2em;'},
], overwrite=False)
    try:
        return display(HTML(d.to_html(escape=False, height=1000)))  
    except:
        return d
