'''Example of Class(Point)'''

class Point:

    def __init__(self,x1,y1,x2,y2):
        self.x1=x1
        self.y1=y1
        self.x2=x2
        self.y2=y2

    #GETTER AND SETTER

    def setx1(self,x1):
        self.x1=x1

    def getx1(self):
        return x1

    def sety1(self,y1):
        self.y1=y1

    def gety1(self):
        return y1

    def setx2(self,x2):
        self.x2=x2

    def getx2(self):
        return x2

    def sety2(self,y2):
        self.y2=y2

    def gety2(self):
        return y2

    #distance

    def distance(self):
        return ((self.x1-self.x2)**2 +(self.y1-self.y2)**2)**0.5

    #slope

    def slope(self):
        return int((self.y1-self.y2)/(self.x1-self.x2))


p1=Point(1,2,3,4)

print("A(",p1.x1,",",p1.y1,")","B(",p1.x2,",",p1.y2,")")

print("Distance two point=",p1.distance())

print("Slope of two point=",p1.slope())

p1.setx1(5)
p1.sety2(8)

print("New slope of two point=",p1.slope())

