'''Inheritance'''

# parent class
class Animal:

    def __init__(self):
        print("Animal created")

    def who(self):
        print("Animal")

    def eat(self):
        print("Eating...")

    def sleep(self):
        print("Sleeping...")


# child class

class Bird(Animal):

    def __init__(self):
        # call super() function
        print("Bird created")
        super().__init__()

    def who(self):
        print("Bird")     

    def fly(self):
        print("Fyling...")


flappy=Bird()

print(flappy.who())

print(flappy.eat())

print(flappy.sleep())

print(flappy.fly())
