def caught_speeding(speed, is_birthday):
    if not is_birthday:
        if speed<=60:
            print("No ticket")
        elif speed>=61 and speed<=80:
            print("Small ticket")
        else:
            print("Big ticket")
    else:
        if speed<=65:
            print("No ticket")
        elif speed>=66 and speed<=85:
            print("Small ticket")
        else:
            print("Big ticket")
