        count += 1
    if flight.WeatherDelay > 0:
        count += 1
    if flight.NASDelay > 0:
        count += 1
    if flight.SecurityDelay > 0:
        count += 1
    if flight.LateAircraftDelay > 0:
        count += 1
    if count > 1:
