def get_sirna_group(experiment, plate, template_sirna_groups=template_sirna_groups):
    sirna_set = get_sirna_set(experiment, plate)

    for i, group in enumerate(template_sirna_groups, 1):
        if sirna_set.issubset(group):
            return i

