def long_life(life):
    if life >= 69.6:
        return 'Long Life expectancy'
    elif life > 64.992799  and life <69.6:
        return 'Longer than normal expectancy'
    elif life  > 59.802000  and life  <= 64.992799:
        return 'Normal life expectancy'
    else:
        return 'Lower life expectancy'
