def get_num_subdomains(netloc: str) -> int:
    subdomain = tldextract.extract(netloc).subdomain 
    if subdomain == "":
        return 0
    return subdomain.count('.') + 1

df_grp['num_subdomains'] = df_grp['netloc'].apply(lambda net: get_num_subdomains(net))

