def RecoverHE(sceneRadiance):
    for i in range(3):
        sceneRadiance[:, :, i] =  cv2.equalizeHist(sceneRadiance[:, :, i])
    return sceneRadiance

def apply_HE(img_path: str):
    img = cv2.imread(img_path)
    sceneRadiance = RecoverHE(img)
    return sceneRadiance
