i = 0
j = 0

for name, dfs in states_grp:

    x = dfs.index
    y = dfs['positiveIncrease']

    if i < 10: 
        ax[i,j].plot(x, abs(y), color='#008080');
        ax[i,j].set_title(f'{name}', fontsize=17);

        ax[i,j].xaxis.set_major_formatter(date_format);
        j += 1
        if j > 4:
            i +=1
            j = 0


