 def func(x):
    if x>90:
        return 'A+'
    elif 80<x<=90:
        return 'A'
    elif 70<x<=80:
        return 'A-'
    elif 60<x<=70:
        return 'B+'
    elif 50<x<=60:
        return 'B'
    elif 40<=x<=50:
        return 'B-'
    else:
        return 'Fail'
df['grade']=df['total_score'].apply(func)
