unique_tokens = set()
all_tokens = []
for title in titles:
    tokens = title.split() # words in Persian are usually seperated by a space character
    all_tokens.extend(tokens)
    for token in tokens:
        unique_tokens.add(token)
for description in descriptions:
    tokens = description.split()
    all_tokens.extend(tokens)
    for token in tokens:
        unique_tokens.add(token)
