def remove_sign(x):
    if x is not np.nan:
        x = x.split("$")[1]
    return x
def remove_comma(x):
    if x is not np.nan:
        x = str(x).replace(",","")
    return x

def to_integer(x):
    if x is not np.nan:
        x = int(x)
    return x

