import random
def getAnswer(answer):
    if answer ==1:
        return 'It is certain'
    elif answer == 2 :
        return 'It is decidedly so'
    elif answer == 3 :
        return 'yes'
    elif answer == 4 :
        return 'Reply hazy try again'
    elif answer == 5 :
        return 'Ask again later'
    elif answer ==6 :
        return 'Concentrate and ask again'
r = random.randint(1,6)
fortune = getAnswer(r)
print(fortune)

