def clean_str(line, keep_eng_and_dig=False):
    line.strip('\n')
    line = re.sub(r"[^0-9a-zA-Z\u4e00-\u9fff]", "", line)
    if keep_eng_and_dig:
        line = re.sub(
        "[\-\s+\.\!\/_,$%^*\(\)\+(+\"\')]+|[+——！，。？、~@#￥%……&*（）<>\[\]:：★◆【】《》;；=?？]+", "", line)
    else:
        line = re.sub(
        "[0-9a-zA-Z\-\s+\.\!\/_,$%^*\(\)\+(+\"\')]+|[+——！，。？、~@#￥%……&*（）<>\[\]:：★◆【】《》;；=?？]+", "", line)
    return line.strip()

def get_stopwords(url):
    with open(url) as f:
        stopwords = [line.strip('\n') for line in f.readlines()]
    return stopwords

