def value_wage(df_value):
    try:
        value = float(df_value[1:-1])
        end = df_value[-1:]

        if end == 'M':
            value = value * 1000000
        elif end == 'K':
            value = value * 1000
    except ValueError:
        value = 0

    return value

df['Value'] = df['Value'].apply(value_wage)
df['Wage'] = df['Wage'].apply(value_wage)
