def find_optimal_lr(recorder):
    """Extract the optimal learning rate from recorder data."""
    optimal_lr = 0
    minimum_loss = float("inf")
    for loss, lr in zip(recorder.losses, recorder.lrs):
        if loss < minimum_loss:
            optimal_lr = lr
            minimum_loss = loss
    return optimal_lr, minimum_loss
