from functools import update_wrapper
def decorator(d):
    "Make function d a decorator: d wraps a function fn."
    def _d(fn):
        return update_wrapper(d(fn), fn)
    update_wrapper(_d, d)
    return _d

@decorator
def memo(f):
    """Decorator that caches the return value for each call to f(args).
    Then when called again with same args, we can just look it up."""
    cache = {}
    def _f(*args):
        try:
            return cache[args]
        except KeyError:
            cache[args] = result = f(*args)
            return result
        except TypeError:
            # some element of args can't be a dict key
            return f(args)
    return _f

@memo
def simulate(total,aces):
    cards=[2,3,4,5,6,7,8,9,10,10,10,10,11]
    result=[]
    for c in cards:
        n=c+total
        new_aces=aces
        if c==11:
            new_aces=new_aces+1
        if new_aces>0 and n>21:
            n=n-10
            new_aces=new_aces-1
        result.append([n,new_aces])
    return(result)
@memo
def simulate_dealer(dealer_total,dealer_aces):
    if dealer_total>21:
        return((0,0,0,0,0,1))
    elif dealer_total>16:
        ls=[0,0,0,0,0,0]
        ls[dealer_total-17]=1
        ls=tuple(ls)
        return(ls)
    else:
        new_state=simulate(dealer_total,dealer_aces)
        ls=(0,0,0,0,0,0)
        for s,a in new_state:
            ls=tuple(a+b/len(new_state) for a,b in zip(ls,simulate_dealer(s,a)))
        return(ls)
@memo    
def win_probability(player_total,dealer_results):
    p=dealer_results[5]
    if player_total>21:
        return(0)
    elif player_total>17:
        for j in dealer_results[0:(player_total-17)]:
            p=p+j
    return(p)

@memo
def simulate_player(player_total,player_aces,dealer_results):
    def win_prob(player_total):
        return(win_probability(player_total,dealer_results))
    if player_total>21:
        return(0)
    else:
        new_state=simulate(player_total,player_aces)
        p=0
        wp=win_prob(player_total)
        for s,a in new_state:
            p=p+simulate_player(s,a,dealer_results)/len(new_state)
        return(max(p,wp))

