def missing_value(df):
    final = []
    empty = {}
    list_of_columns = list(df.columns)
    for column in range(0,len(list_of_columns)):
        data = set(df[list_of_columns[column]].isna())
        empty.update({
            list_of_columns[column]:data
        })
    final.append(empty)
    return final
