# This function calculates the number of games missed and appends it to our dataframe
def countGamesMissed(df):

    GM_array = []
    for i, row in df.iterrows():

        DM1  = row['DM_M1']
        DM7  = row['DM_M7']
        DM28 = row['DM_M28']
        DM42 = row['DM_M42']

        if DM42 == 1:
            GM_array.append(6.5)
        elif DM28 == 1:
            GM_array.append(4.5)
        elif DM7 == 1:
            GM_array.append(1.5)
        elif DM1 == 1:
            GM_array.append(.5)
        else:
            GM_array.append(0)

    df['GamesMissed'] = GM_array
    return df
